<?phpnamespace App;use App\Controller\Controller;abstract class BaseController extends Controller{    protected string $title;    protected string $content;    protected string $id_article;    protected string $id_forums;    protected string $id_threads;    protected string $id_posts;    protected string $msg_count;    protected ?array $user = null;    private array $check = [];    public function __construct()    {        session_start();    }    //    // Виртуальный обработчик запроса    //    protected function OnInput(): void    {        $this->title = 'Форум';        $this->id_article = 'null';        $this->id_forums = 'null';        $this->id_threads = 'null';        $this->id_posts = 'null';        $this->content = '';        $this->msg_count = '';        $users = Users::Instance();        // Очистка старых сессий        $users->ClearSessions();        // Текущий пользователь        $this->user = $users->Get();        // Кол-во сообщений        $result = $users->message_count();        $this->check = $result ? $result->fetch() : [];        if (!empty($this->check['count'])) {            $this->msg_count = "(" . $this->check['count'] . ")";        }    }    //    // Виртуальный генератор HTML    //    protected function OnOutput(): void    {        $vars = [            'title' => $this->title,            'content' => $this->content,            'id_article' => $this->id_article,            'msg_count' => $this->msg_count,            'id_forums' => $this->id_forums,            'id_thread' => $this->id_threads,            'id_posts' => $this->id_posts,            'user' => $this->user,        ];        $page = $this->Template('templates/v_main.php', $vars);        echo $page;    }}