<?phpnamespace App\Controller;abstract class Controller{    /**     * Обработка HTTP-запроса.     */    public function Request(): void    {        $this->OnInput();        $this->OnOutput();    }    /**     * Виртуальный обработчик входных данных (переопределяется в потомках).     */    protected function OnInput(): void    {        // По умолчанию — ничего    }    /**     * Виртуальный генератор HTML-вывода (переопределяется в потомках).     */    protected function OnOutput(): void    {        // По умолчанию — ничего    }    /**     * Проверка, является ли запрос GET.     */    protected function IsGet(): bool    {        return $_SERVER['REQUEST_METHOD'] === 'GET';    }    /**     * Проверка, является ли запрос POST.     */    protected function IsPost(): bool    {        return $_SERVER['REQUEST_METHOD'] === 'POST';    }    /**     * Рендеринг HTML-шаблона с переменными.     *     * @param string $fileName     * @param array $vars     * @return string     */    protected function Template(string $fileName, array $vars = []): string    {        extract($vars, EXTR_SKIP); // безопаснее, чем foreach        ob_start();        include $fileName;        return ob_get_clean();    }}