<?phpdeclare(strict_types=1);namespace App;class EditController extends BaseController{    protected string $id_forums;    private ?string $forum_name = null;    private ?string $description = null;    private Database $database;    public function __construct()    {        parent::__construct();        $this->database = new Database();    }    protected function OnInput(): void    {        parent::OnInput();        $this->title .= ' :: Редактирование форума';        if ($this->IsPost()) {            // Получаем параметры с валидацией            $forum_name = $_POST['forum_name'] ?? '';            $description = $_POST['description'] ?? '';            $id_forums = isset($_GET['forum']) ? (int)$_GET['forum'] : 0;            if ($id_forums > 0 && $forum_name !== '' && $description !== '') {                $this->database->forum_update($id_forums, $forum_name, $description);                header('Location: index.php');                exit();            } else {                $this->error = 'Некорректные данные формы';                // Можно добавить обработку ошибки            }        } else {            $id_forums = isset($_GET['forum']) ? (int)$_GET['forum'] : 0;            if ($id_forums > 0) {                $res = $this->database->forums_select_forum($id_forums)->fetch();                if ($res) {                    $this->forum_name = $res['forum_name'] ?? null;                    $this->description = $res['description'] ?? null;                    $this->id_forums = (string) $id_forums;                } else {                    $this->error = 'Форум не найден';                    // Можно делать редирект или показывать ошибку                }            } else {                $this->error = 'Не указан ID форума';            }        }    }    protected function OnOutput(): void    {        $vars = [            'description' => $this->description,            'forum_name' => $this->forum_name,            'id_forums' => $this->id_forums,            'error' => $this->error ?? null,        ];        $this->content = $this->Template('templates/v_edit.php', $vars);        parent::OnOutput();    }}