<?phpnamespace App;class ViewController extends BaseController{    public ?array $user = null;    public int $online = 0;    public array $online_user = [];    private array $array_forums = [];    private Database $database;    public function __construct()    {        parent::__construct();        $this->database = new Database();    }    protected function OnInput(): void    {        parent::OnInput();        $this->title .= ' :: Список форумов';        $users = Users::Instance();        $users->ClearSessions();        $this->user = $users->Get();        $onlineUsers = $users->Can();        $this->online = is_array($onlineUsers) ? count($onlineUsers) : 0;        $this->online_user = $users->IsOnline();        $this->array_forums = $this->database->forums_select();    }    protected function OnOutput(): void    {        $vars = [            'array_forums' => $this->array_forums,            'user' => $this->user,            'online' => $this->online,            'online_user' => $this->online_user,            'array_podforums' => $this->array_podforums ?? [],        ];        $this->content = $this->Template('templates/v_view.php', $vars);        parent::OnOutput();    }}