<?phpinclude_once('include/class_view.php');include_once('include/class_showforum.php');include_once('include/class_edit.php');include_once('include/class_delete.php');include_once('include/class_add.php');include_once('include/class_confirm.php');include_once('include/class_login.php');include_once('include/class_message.php');include_once('include/class_showmessage.php');include_once('include/class_editthread.php');include_once('include/class_addthread.php');include_once('include/class_addpost.php');include_once('include/class_editpost.php');include_once('include/class_register.php');include_once('include/class_addmessage.php');switch ($_GET['c']){case 'showforum':	$controller = new C_ShowForum();	break;	case 'showmessage':	$controller = new C_ShowMessage();	break;	case 'edit':	$controller = new C_Edit();	break;	case 'editthread':	$controller = new C_EditThread();	break;		case 'editpost':	$controller = new C_EditPost();	break;	case 'delete':	$controller = new C_Delete();		break;case 'add':	$controller = new C_Add();	break;	case 'addthread':	$controller = new C_AddThread();	break;	case 'addpost':	$controller = new C_AddPost();	break;	case 'confirm':	$controller = new C_Confirm();	break;case 'auth':	$controller = new C_Auth();		break;case 'mail':	$controller = new C_Message();		break;case 'register':	$controller = new C_Register();	break;	case 'addmessage':	$controller = new C_AddMessage();		break;default:		$controller = new C_View();}$controller->Request();