<?php// Автоматическая загрузка классов с помощью Composeruse App\AddController;use App\AddMessageController;use App\AddPostController;use App\AddThreadController;use App\ConfirmController;use App\DeleteController;use App\EditController;use App\EditPostController;use App\EditThreadController;use App\LoginController;use App\MessageController;use App\RegisterController;use App\ShowForumController;use App\ShowMessageController;use App\ViewController;require_once __DIR__ . '/vendor/autoload.php';// Список доступных контроллеров$controllers = [    'showforum' => ShowForumController::class,    'showmessage' => ShowMessageController::class,    'edit' => EditController::class,    'editthread' => EditThreadController::class,    'editpost' => EditPostController::class,    'delete' => DeleteController::class,    'add' => AddController::class,    'addthread' => AddThreadController::class,    'addpost' => AddPostController::class,    'confirm' => ConfirmController::class,    'auth' => LoginController::class,    'mail' => MessageController::class,    'register' => RegisterController::class,    'addmessage' => AddMessageController::class,];// Получаем параметр из GET-запроса$controllerKey = $_GET['c'] ?? '';// Определяем класс контроллера$controllerClass = $controllers[$controllerKey] ?? ViewController::class;// Создаем экземпляр и запускаем обработку$controller = new $controllerClass();$controller->Request();