<?phpuse JBBCode\Parser;use JBBCode\DefaultCodeDefinitionSet;$start = microtime(true);$parser = new Parser();$parser->addCodeDefinitionSet(new DefaultCodeDefinitionSet());// Получаем текст BBCode методом POST$text = $_POST['text'] ?? '';// Формируем набор смайликов$pak = file('templates/smilies/Set_Smiles_YarNET.pak');$smiles = [];foreach ($pak as $val) {    $val = trim($val);    if (!$val || $val[0] === '#') continue;    list($gif, $alt, $symbol) = explode('=+:', $val);    $smiles[$symbol] = '<img src="templates/smilies/' . htmlspecialchars($gif) . '" alt="' . htmlspecialchars($alt) . '">';}// Заменяем смайлы в тексте$textWithSmiles = strtr($text, $smiles);// Парсим BBCode$parser->parse($textWithSmiles);$parsedHtml = $parser->getAsHtml();?><link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet"><div class="container mt-4">    <div class="card">        <div class="card-header bg-primary text-white">            Просмотр сообщения        </div>        <div class="card-body">            <div class="postbody">                <?= $parsedHtml ?>            </div>        </div>    </div>    <form method="post" class="mt-4">        <div class="mb-3">            <label for="otv" class="form-label">Имя получателя:</label>            <select name="otv" class="form-select">                <?php foreach ($res as $row): ?>                    <option value="<?= $row['id_user'] ?>"><?= htmlspecialchars($row['name']) ?></option>                <?php endforeach; ?>            </select>        </div>        <div class="mb-3">            <label class="form-label">Заголовок сообщения:</label>            <textarea name="theme" class="form-control" rows="1"></textarea>        </div>        <div class="mb-3">            <label class="form-label">Сообщение:</label>            <textarea name="text" id="text" class="form-control" rows="10"><?= htmlspecialchars($text) ?></textarea>        </div>        <div class="d-flex gap-2">            <input type="submit" name="submitForm" class="btn btn-success" value="Отправить" />            <input type="submit" name="ok" class="btn btn-warning" value="Предварительный просмотр" />            <button type="button" class="btn btn-secondary" onclick="javascript:history.go(-1)">Отмена</button>        </div>    </form></div>