<?phpuse JBBCode\Parser;use JBBCode\DefaultCodeDefinitionSet;$start = microtime(true);$text = $_POST['text'] ?? '';$preview = '';if (isset($_POST['ok'])) {    $parser = new Parser();    $parser->addCodeDefinitionSet(new DefaultCodeDefinitionSet());    $parser->parse($text);    $preview = $parser->getAsHtml();}?><link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/css/bootstrap.min.css" rel="stylesheet"><link href="style1.css" rel="stylesheet" type="text/css"><div class="container mt-5">    <div class="card shadow-sm">        <div class="card-body">            <h5 class="card-title">Форма добавления сообщения</h5>            <?php if ($preview): ?>                <div class="alert alert-secondary mt-3">                    <strong>Предпросмотр:</strong><br>                    <div><?= $preview ?></div>                </div>            <?php endif; ?>            <form method="post" action="">                <div class="mb-3">                    <label for="text" class="form-label">Сообщение (BBCode)</label>                    <textarea class="form-control" id="text" name="text" rows="10"><?= htmlspecialchars($text) ?></textarea>                </div>                <button type="submit" name="submitForm" class="btn btn-primary">Отправить</button>                <button type="submit" name="ok" class="btn btn-outline-secondary">Предпросмотр</button>                <a href="javascript:history.back()" class="btn btn-link">Отмена</a>            </form>        </div>    </div></div>