<?phpuse JBBCode\Parser;use JBBCode\DefaultCodeDefinitionSet;$text = $_POST['text'] ?? '';$parser = new Parser();$parser->addCodeDefinitionSet(new DefaultCodeDefinitionSet());$parser->parse($text);$parsedHtml = $parser->getAsHtml();?><link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet"><div class="container mt-4">    <div class="card">        <div class="card-header bg-primary text-white">            Создание темы        </div>        <div class="card-body">            <form method="post">                <div class="mb-3">                    <label for="theme" class="form-label">Название темы</label>                    <input type="text" class="form-control" name="theme" id="theme">                </div>                <div class="mb-3">                    <label for="description" class="form-label">Описание темы</label>                    <input type="text" class="form-control" name="description" id="description">                </div>                <div class="mb-3">                    <label for="text" class="form-label">Текст сообщения (BBCode)</label>                    <textarea class="form-control" id="text" name="text" rows="8"><?php echo htmlspecialchars($text); ?></textarea>                </div>                <button type="submit" name="submitForm" class="btn btn-success">Отправить</button>                <button type="submit" name="ok" class="btn btn-secondary">Предпросмотр</button>                <a href="javascript:history.back()" class="btn btn-outline-danger">Отмена</a>            </form>            <?php if (isset($_POST['ok'])): ?>                <div class="mt-4">                    <h5>Предпросмотр</h5>                    <div class="border rounded p-3 bg-light">                        <?php echo $parsedHtml; ?>                    </div>                </div>            <?php endif; ?>        </div>    </div></div>