<?php/** * Шаблон редактора форума с Bootstrap 5 * ====================================== * Переменные: *   - $forum_name (string|null) — название форума *   - $description (string|null) — описание форума */// Функция для безопасного вывода текста в HTMLfunction e(?string $str): string {    return htmlspecialchars($str ?? '', ENT_QUOTES | ENT_SUBSTITUTE, 'UTF-8');}?><form method="post" class="container mt-4" style="max-width: 600px;">    <div class="mb-3">        <label for="forum_name" class="form-label">Название форума</label>        <textarea id="forum_name" name="forum_name" class="form-control" rows="1"><?= e($forum_name) ?></textarea>    </div>    <div class="mb-3">        <label for="description" class="form-label">Описание форума</label>        <textarea id="description" name="description" class="form-control" rows="3"><?= e($description) ?></textarea>    </div>    <button type="submit" class="btn btn-primary me-2">Сохранить</button>    <button type="button" class="btn btn-secondary" onclick="history.back();">Отмена</button></form>