<?phpuse JBBCode\Parser;use JBBCode\DefaultCodeDefinitionSet;$parsedHtml = '';if (!empty($text)) {    $parser = new Parser();    $parser->addCodeDefinitionSet(new DefaultCodeDefinitionSet());    $parser->parse($text);    $parsedHtml = $parser->getAsHtml();}?><!DOCTYPE html><html lang="ru"><head>    <meta charset="UTF-8">    <title>Редактирование</title>    <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet"></head><body class="bg-light py-4"><div class="container">    <div class="card shadow-sm">        <div class="card-body">            <h5 class="card-title">Редактировать сообщение</h5>            <?php if ($parsedHtml): ?>                <div class="alert alert-secondary">                    <strong>Предпросмотр:</strong>                    <div class="mt-2 border rounded p-2 bg-white">                        <?= $parsedHtml ?>                    </div>                </div>            <?php endif; ?>            <form method="post">                <div class="mb-3">                    <label for="text" class="form-label">Текст (BBCode):</label>                    <textarea class="form-control" name="text" id="text" rows="10"><?= htmlspecialchars($text) ?></textarea>                </div>                <div class="d-flex gap-2">                    <button type="submit" name="ok" class="btn btn-outline-primary">Предпросмотр</button>                    <button type="submit" name="submitForm" class="btn btn-primary">Отправить</button>                    <button type="button" class="btn btn-secondary" onclick="history.back()">Отмена</button>                </div>            </form>        </div>    </div></div><script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/js/bootstrap.bundle.min.js"></script></body></html>