      $("#validEmail").css("background-image", "url('images/validyes.png')");                    } else {                        $("#validEmail").css("background-image", "url('images/validno.png')");                    }                } else {                    $("#validEmail").css("background-image", "none");                }            });        });        function isValidEmailAddress(emailAddress) {            var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);            return pattern.test(emailAddress);        }    </script></head><body><div id="header" class="mb-3">    <!-- Место для шапки --></div><div id="menu" class="d-flex align-items-center">    <a href="index.php" class="me-3">Форум</a>    <div class="ms-auto">        <a href="index.php?c=mail">Личные сообщения <?= htmlspecialchars($msg_count) ?></a>        |        <?php if (!empty($user) && $user != 0): ?>            <a href="index.php?c=auth">Выход</a>        <?php else: ?>            <a href="index.php?c=auth">Авторизация</a> |            <a href="index.php?c=register">Регистрация</a>        <?php endif; ?>    </div></div><div id="content" class="container">    <?= $content ?></div><div id="footer" class="mt-4">    Все права защищены. Адрес. Телефон.</div><!-- Bootstrap 5 JS Bundle (Popper + JS) --><script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/js/bootstrap.bundle.min.js"></script></body></html>