<?phpinclude_once('functions/functions.php');require_once 'bbcode.lib.php';/** * Шаблон главной страницы * ======================= * */// Формируем набор смайликов$pak    = file('templates/smilies/Set_Smiles_YarNET.pak');$smiles = array();foreach ($pak as $val) {    $val = trim($val);    if (!$val || '#' == $val{0}) {        continue;    }    list($gif, $alt, $symbol) = explode('=+:', $val);    $smiles[$symbol] = '<img src="templates/smilies/' . htmlspecialchars($gif)            . '" alt="' . htmlspecialchars($alt) . '" />';}// Создаем объект и парсим $text$bb              = new bbcode($text);// Задаем набор смайликов$bb->mnemonics = $smiles;if (isset($_POST['highlight'])) {    // Подсвечиваем и выводим BBCode    echo $bb->highlight();} else {    // Конвертируем BBCode в HTML и выводим его    echo $bb->get_html();}?><?php if ($user[0] <> 0): {?><a href="index.php?c=addpost&forums=<?= $_GET['forums'] ?>&id=<?= $_GET['id'] ?>">Добавить cообшение(Ответить)</a></div></div><?php } endif; ?><div class="borderwrap" style="display:" id="fo_1">  <table class='ipbtable' cellspacing="1">    <?php if ($array_messages[0] == ""): { echo ""; } else: {?>    <tr>      <th colspan="2" width="66%">Ответы пользователей</th>    </tr>    <?php } endif; ?>    Основной пост темы:<br><br>    <?php foreach ($array_thread as $row2): ?><?= $row2['date'] ?><br>    <?php $bb = new bbcode($row2['text']); echo $bb -> get_html();?><hr>    <?php endforeach; ?>    <?php if (!isset($_GET['showforum'])): {foreach ($array_messages as $row=>$key): ?>    <tr>      <td align="center" class="row2" width="1%"><img src='images/norm.gif' border='0' /></td>      <td class="row2">        Дата сообщения: <?= $key['post_date'] ?><br>        Оставил сообщение: <?= $key['login'] ?>        <b><?= $key['header'] ?></b>        <br />        <span class="forumdesc"><?php $bb = new bbcode($key['post_text']); echo $bb->get_html(); ?>          <br />          <?php if ($user[0] <> 0): {?>          <a href="index.php?c=confirm&forums=<?= $_GET['forums'] ?>&post=<?= $key['id_posts'] ?>&threads=<?= $_GET['id'] ?>">Удалить</a> | <a href="index.php?c=editpost&post=<?= $key['id_posts'] ?>">Редактировать</a>          <?php } endif; ?>        </span>        <hr><br>      </td>    </tr>    <?php endforeach; } endif; ?>  </table>  <?php if ($nextpage == "") echo ""; else echo " ".$pervpage.$page2left.$page1left.'<b>'.$page.'</b>'.$page1right.$page2right.$nextpage; ?>