<?php/** * Шаблон главной страницы * ======================= */?><div class="container mt-3">    <?php if (!empty($user) && $user != 0): ?>        <div class="mb-3">            <a href="index.php?c=add" class="btn btn-primary btn-sm">Добавить раздел</a>        </div>    <?php endif; ?>    <div class="border p-3 mb-4" id="fo_1">        <table class="table table-striped table-bordered ipbtable">            <thead class="table-light">            <tr>                <th colspan="2" style="width:66%;">Форум</th>                <th class="text-center" style="width:7%;">Тем</th>                <th class="text-center" style="width:7%;">Ответов</th>                <th style="width:35%;">Последнее сообщение</th>            </tr>            </thead>            <tbody>            <?php if (!isset($_GET['showforum'])): ?>                <?php foreach ($array_forums as $row): ?>                    <tr>                        <td class="align-middle text-center" style="width:1%;">                            <img src="images/<?= htmlspecialchars($row['icon_img']) ?>" alt="" />                        </td>                        <td class="align-middle">                            <b><a href="index.php?c=showforum&id=<?= (int)$row['id_forums'] ?>&page=1"><?= htmlspecialchars($row['forum_name']) ?></a></b>                            <br />                            <span class="forumdesc">                                    <?= nl2br(htmlspecialchars($row['description'])) ?>                                <?php if (!empty($user) && $user != 0): ?>                                    <br />                                    <a href="index.php?c=confirm&forum=<?= (int)$row['id_forums'] ?>">Удалить</a> |                                        <a href="index.php?c=edit&forum=<?= (int)$row['id_forums'] ?>">Редактировать</a>                                <?php endif; ?>                                </span>                        </td>                        <td class="align-middle text-center"><?= (int)$row['threads_quantity'] ?></td>                        <td class="align-middle text-center"><?= (int)$row['posts_quantity'] ?></td>                        <td class="align-middle" nowrap="nowrap">                            <?php if (empty($row['last_author'])): ?>                                <span>Сообщений нет...<br /><b></b></span>                            <?php else: ?>                                <span><?= htmlspecialchars($row['last_post']) ?><br /><b>Автор:</b> <?= htmlspecialchars($row['last_author']) ?></span>                            <?php endif; ?>                        </td>                    </tr>                <?php endforeach; ?>            <?php endif; ?>            </tbody>        </table>        <?php if (!empty($user) && $user != 0): ?>            <hr />            <div class="mb-2">                <strong>Статистика:</strong><br />                Сейчас на форуме: <?= (int)$online ?> человек(а)<br />                Пользователи на сайте:                <?php if (!empty($user) && !empty($user['name'])): ?>                    <?= htmlspecialchars($user['name']) ?>                <?php endif; ?>                <?php if (!empty($online_user)): ?>                    <?php foreach ($online_user as $row1): ?>                        <?= ", " . htmlspecialchars($row1['name']) ?>                    <?php endforeach; ?>                <?php endif; ?>            </div>        <?php endif; ?>    </div></div>